
public class IndexOf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="narendra";
		System.out.println(s.indexOf("r"));
		System.out.println(s.indexOf("R"));
		
		System.out.println(s.indexOf("re"));
		System.out.println(s.indexOf('r',4));


		

	}

}
